import { compose } from "redux";
import { connect } from "react-redux";
import { setUserInfoThunk } from "../../Store/thunks";
import Header from "./Header";

let mapStateToProps = (state) => {
  console.log(state)
  return {
    events:state.events.events.length,
    followers:state.followers.followers.length,
    repos: state.respositories.respositories.length,
    events_url: state.userInfo.user_info.events_url ? state.userInfo.user_info.events_url.split("{")[0]: null,
  };
};
let mapDispatchToProps = (dispatch) => {
  return {
    setUserEvents: (events_url) => {
      setUserInfoThunk(dispatch, events_url);
    },
  };
};

export default compose(connect(mapStateToProps, mapDispatchToProps))(Header);
