import { compose } from "redux";
import { connect } from "react-redux";
import FollowersList from "./FollowersList";
import { setUserFollowersThunk } from "../../Store/thunks";

let mapStateToProps = (state) => {
  console.log(state);
  return {
    id: state.followers.followers.id,
    // company: state.followers.followers.company,
    // email: state.followers.followers.email,
    // avatar_url: state.followers.followers.avatar_url,
    // blog: state.followers.followers.blog,
    // repos_url: state.followers.followers.repos_url,
  };
};
let mapDispatchToProps = (dispatch) => {
  return {
    setUserFollowers: () => {
      setUserFollowersThunk(dispatch);
    },
  };
};

export default compose(connect(mapStateToProps, mapDispatchToProps))(
  FollowersList
);
